import React, { Component } from 'react'
import { connect } from "react-redux";
class Ticket extends Component {
  renderListTicket = () => {
    return this.props.ticket.map((item) => (
      <tr>
      <td>
          <textarea id="nameDisplay">{item.Name}</textarea>
      </td>
      <td>
          <textarea id="NumberDisplay">{item.Number}</textarea>
      </td>
      <td>
          <textarea id="seatsDisplay">{item.Seats}</textarea>
      </td>
  </tr>
    ));
};
  render() {
    return (
        <div class="displayerBoxes txt-center">
        <table class="Displaytable w3ls-table" width="100%">
            <tr>
                <th>Name</th>
                <th>Number of Seats</th>
                <th>Seats</th>
            </tr>
            Render
        </table>
    </div>
    )
  }
}
let mapStateToProps = (state) => {
    return {
      cart: state.ticket,
    };
  };

  export default connect(mapStateToProps)(Ticket);
