import React, { Component } from 'react'

export default class List extends Component {

    render() {
        const SeatChart = () => {
            const rows = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J'];
            const seatNumbers = Array.from({ length: 12 }, (_, index) => index + 1);

            const renderSeats = () => {
                return rows.map((row) => (
                    <tr key={row}>
                        <td>{row}</td>
                        {seatNumbers.map((seatNumber) => (
                            <td key={`${row}${seatNumber}`}>
                                <input type="checkbox" className="seats" value={`${row}${seatNumber}`} />
                            </td>
                        ))}
                    </tr>
                ));
            };
            return (
                <div> <ul class="seat_w3ls">
                    <li class="smallBox greenBox">Selected Seat</li>

                    <li class="smallBox redBox">Reserved Seat</li>

                    <li class="smallBox emptyBox">Empty Seat</li>
                </ul>
                    <div className="seatStructure txt-center" style={{ overflowX: 'auto' }}>
                        <table id="seatsBlock">
                            <tbody>
                                <tr>
                                    <td></td>
                                    {seatNumbers.map((seatNumber) => (
                                        <td key={seatNumber}>{seatNumber}</td>
                                    ))}
                                </tr>
                                {renderSeats()}
                            </tbody>
                        </table>

                        <div className="screen">
                            <h2 className="wthree">Screen this way</h2>
                        </div>
                        <button>Confirm Selection</button>
                    </div>
                </div>
            )
        }
    }
}
