import React, { Component } from 'react'
import { connect } from "react-redux";
class Ticket extends Component {
  renderListGhe = () => {
    return this.props.gheArr.slice(1).map((hang) => (
        <tr>
            <td key={hang.hang}></td>
            <h2>{hang.hang}</h2>
            {hang.danhSachGhe.map((ghe) => (
                <Item key={ghe.soGhe} data={ghe} />
            ))}
        </tr>
    ));
};
  render() {
    return (
        <div class="displayerBoxes txt-center">
        <table class="Displaytable w3ls-table" width="100%">
            <tr>
                <th>Name</th>
                <th>Number of Seats</th>
                <th>Seats</th>
            </tr>
            <tr>
                <td>
                    <textarea id="nameDisplay"></textarea>
                </td>
                <td>
                    <textarea id="NumberDisplay"></textarea>
                </td>
                <td>
                    <textarea id="seatsDisplay"></textarea>
                </td>
            </tr>
        </table>
    </div>
    )
  }
}
let mapStateToProps = (state) => {
    return {
      cart: state.ticket,
    };
  };

  export default connect(mapStateToProps)(Ticket);
