import React, { Component } from 'react';
import { connect } from "react-redux";
import Item from './Item';
class List extends Component {

    renderListGhe = () => {
        return this.props.gheArr.map((hang) => (
            <div key={hang.hang}>
                <h2>{hang.hang}</h2>
                {hang.danhSachGhe.map((ghe) => (
                    <Item key={ghe.soGhe} data={ghe} />
                ))}
            </div>
        ));
    };
    render() {
        return <div className='seatStructure txt-center'>
            <ul class="seat_w3ls">
                <li class="smallBox greenBox">Selected Seat</li>

                <li class="smallBox redBox">Reserved Seat</li>

                <li class="smallBox emptyBox">Empty Seat</li>
            </ul>
            <table id="seatsBlock"></table>
            {this.renderListGhe()}
            <div class="screen">
                <h2 class="wthree">Screen this way</h2>
            </div>
            <button onclick="updateTextArea()">Confirm Selection</button>
        </div>;
    };


}

let mapStateToProps = (state) => {
    return {
        gheArr: state.gheArr,
    };
};
export default connect(mapStateToProps)(List);
