import React, { Component } from 'react';
import { connect } from "react-redux";
class List extends Component {
    renderListShoe = () => {
      // Sắp xếp mảng theo thuộc tính "Hang"
      const sortedShoeArr = this.props.shoeArr.sort((a, b) => (a.hang > b.hang) ? 1 : -1);
  
      return sortedShoeArr.map((item) => {
        return (
          <div key={item.hang}>
            <h2>{item.hang}</h2>
            {/* Render danh sách giày trong mỗi hàng */}
            {item.danhSachGhe.map((ghe) => (
              <Item key={ghe.soGhe} data={ghe} />
            ))}
          </div>
        );
      });
    };
  
    render() {
      return <div className="row col-7">{this.renderListShoe()}</div>;
    }
  }

let mapStateToProps = (state) => {
    return {
      shoeArr: state.shoeArr,
    };
  };
  export default connect(mapStateToProps)(List);
